import React, { useState, useEffect } from "react";
import Papa from "papaparse";
import Football_Data from "../csvs/football_data.csv";

function Country({formData, setFormData}){
    const [countries, setCountries] = useState([]);
    const { country } = formData;

    useEffect(() => {
        const fetchData = async () => {
          try {

            if(country == "Football"){
                const response = await fetch(Football_Data);
            } else if (country == "Basketball") {
                const response = await fetch(Basketball_Data);
            } else if (country == "Handball") {
                const response = await fetch(Handball_Data);
            } else if (country == "Volleyball") {
                const response = await fetch(Volleyball_Data);
            } 

            const data = await response.text();

            Papa.parse(data, {
              header: true,
              dynamicTyping: true,
              complete: (result) => {
                if (result.data) {
                  const countriesFromCSV = result.data.map((row) => row.Opponent);
                  setCountries(countriesFromCSV);
                }
              },
            });
          } catch (error) {
            console.error("Error fetching data:", error);
          }
        };

        fetchData();
      }, []);

    return(
        <div className="discipline-component">
            {/* <input type="text" placeholder="Discipline..." 
            value={formData.discipline} 
            onChange={(event) => setFormData({ ...formData, discipline: event.target.value })} /> */}
            <div>
            {countries.map((country, index) => (
          <button
            key={index}
            onClick={() => setFormData({ ...formData, country })}
            className={formData.country === country ? "selected" : ""}
          >
            {country}
          </button>
        ))}
            </div>
        </div>
    )
}

export default Country;
