import React from "react";

function Discipline({formData, setFormData}){

    const [showOptions, setShowOptions] = useState(false);

  const handleButtonClick = () => {
    setShowOptions(!showOptions);
  };

  const handleOptionClick = (selectedDiscipline) => {
    setFormData({ ...formData, discipline: selectedDiscipline });
    setShowOptions(false);
  };

    return(
        <div className="discipline-component">
            {/* <input type="text" placeholder="Discipline..." 
            value={formData.discipline} 
            onChange={(event) => setFormData({ ...formData, discipline: event.target.value })} /> */}
            <button type="button" onClick={handleButtonClick}>
        {formData.discipline || 'Select Discipline'}
      </button>

      {showOptions && (
        <div>
          <p onClick={() => handleOptionClick('discipline1')}>Discipline 1</p>
          <p onClick={() => handleOptionClick('discipline2')}>Discipline 2</p>
          {/* Dodaj więcej opcji według potrzeb */}
        </div>
      )}
        </div>
    )
}

export default Discipline;
