import React from "react";
import Papa from 'papaparse';

function Country({formData, setFormData}){
    const conutrys = ["France", "Germany", "Portugal", "Denmark"]; // Dodaj dowolne dyscypliny, jakie chcesz

    const [csvData, setCsvData] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
          try {
            const response = await fetch(process.env.PUBLIC_URL + '/football_data.csv');
            const reader = response.body.getReader();
            const result = await reader.read();
            const text = new TextDecoder().decode(result.value);

            Papa.parse(text, {
              complete: (parsedData) => {
                console.log(parsedData.data);
                setCsvData(parsedData.data);
              },
              header: true,
            });
          } catch (error) {
            console.error('Error fetching CSV data:', error);
          }
        };

        fetchData();
      }, []);

    return(
        <div className="discipline-component">
