import React, { useState } from "react";

function Discipline({formData, setFormData}){
    const disciplines = ["Football", "Basketball", "Handball", "Volleyball"]; // Dodaj dowolne dyscypliny, jakie chcesz

    return(
        <div className="discipline-component">
            {/* <input type="text" placeholder="Discipline..." 
            value={formData.discipline} 
            onChange={(event) => setFormData({ ...formData, discipline: event.target.value })} /> */}
            <div className="buttons">
                {disciplines.map((discipline, index) => (
                    <button
                        key={index}
                        onClick={() => setFormData({ ...formData, discipline })}
                        className={formData.discipline === discipline ? "selected" : ""}
                    >
                    {discipline}
                    </button>
                ))}
            </div>
        </div>
    )
}

export default Discipline;
