import { useState, useEffect } from "react";
import React from "react";

function Result({formData, setFormData}){

    const { discipline, country } = formData;

    const [data, setData] = useState({ discipline: "", opponent: "", result: "" });
    discipline = discipline.toLowerCase();

    useEffect(() => {
            const fetchData = () => {
            fetch(`discipline/${discipline}/opponent/${country}`)
            .then((response) => {
                if(!response.ok){
                throw new Error(`HTTP error! Status: ${response.status}`);
                }
                return response.json();
            })
            .then((data) => {
                console.log("Received data:", data);
                setData(data);
            })
            .catch((error) => {
                console.error('Error: ', error);
                setData({
                discipline: "N/A",
                opponent: "N/A",
                result: "There is no such discipline or country in the database!",
            });
            });
        } 

        fetchData();
    }, [country, discipline]);

    return(
        <div className="result-component">
            {discipline}
            {country}
            {data.result}
        </div>
    )
}

export default Result;
